(333, 1, '2017-10-30 11:31:14', '2017-10-30 11:31:14', '<style>\r\ndiv.item {\r\n    vertical-align: top;\r\n    display: inline-block;\r\n    text-align: center;\r\n    width: 360px;\r\n}\r\nimg {\r\n    background-color: grey;\r\n}\r\n\r\na {\r\n    text-decoration: none;\r\n}\r\nh2 {\r\n    text-align: center;\r\n}\r\n\r\n</style>\r\n\r\n[insert_php]\r\n$link = mysqli_connect(\"localhost\", \"root\", \"\", \"wordpress\");\r\n\r\n/* check connection */\r\nif (mysqli_connect_errno()) {\r\n    printf(\"Connect failed: %s\\n\", mysqli_connect_error());\r\n    exit();\r\n}\r\n\r\n$query = \"SELECT * FROM projects ORDER by proj_goal-proj_fund\";\r\n\r\nif ($result = mysqli_query($link, $query)) {\r\n\r\n    /* fetch associative array */\r\n    while ($row = mysqli_fetch_assoc($result)) {\r\n    printf (\"<div class=\\\"item\\\">\r\n    <a href=\\\"http://localhost/wordpress/projinfo/?view=%s\\\">\r\n    <img src=\\\"../wp-content/uploads/users/%s\\\"><br>\r\n    %s <br> by %s\r\n    </a>\r\n    <p style=\\\"overflow: hidden; display: -webkit-box; -webkit-line-clamp: 4; -webkit-box-orient: vertical;\\\">%s</p>\r\n    </div>\", $row[\"proj_title\"],$row[\"proj_image\"],$row[\"proj_title\"],$row[\"proj_user\"],$row[\"proj_info\"]);\r\n    }\r\n\r\n    /* free result set */\r\n    mysqli_free_result($result);\r\n}\r\n\r\n/* close connection */\r\nmysqli_close($link);\r\n[/insert_php]', 'Projects', '', 'inherit', 'closed', 'closed', '', '226-revision-v1', '', '', '2017-10-30 11:31:14', '2017-10-30 11:31:14', '', 226, 'http://localhost/wordpress/226-revision-v1/', 0, 'revision', '', 0),
